import "../css/activeChatInfoModal.dev.css"

let userChatInfoBtnContainerHtml = `
<div class="chat-info-btn">${svg_deleteBlankBtn} Delete Chat </div>

<div class="chat-info-btn">${svg_clearChat} Clear Chat History </div>

<div class="chat-info-btn ">${svg_changeAppearance} Change Chat Appearance</div>

<div class="chat-info-btn">${svg_shareBlankBtn} Share User Profile </div>
`

let groupChatInfoBtnContainerHtml = `
<div class="chat-info-btn chat-info-btn--admin" id="chatInfoEditGroupChatBtn">${svg_editGroup} Edit Chat </div>

<div class="chat-info-btn chat-info-btn--admin" id="chatInfoDeleteChatBtn">${svg_deleteBlankBtn} Delete Chat </div>


<div class="chat-info-btn chat-info-btn--member">${svg_leaveGroupBtn} Leave Group </div>

<div class="chat-info-btn " id="chatInfoClearChatHistoryBtn">${svg_clearChat} Clear Chat History </div>

<div class="chat-info-btn chat-info-btn--admin">${svg_changeAppearance} Change Chat Appearance</div>
`

export async function createActiveChatInfoModal(chatId) {
  const activeChatSection = document.getElementById("activeChatSection")
  let activeChatId = activeChatSection.dataset.chatId
  if (activeChatId !== "" && activeChatId.toString() === chatId.toString()) {
    let activeChatInfoModal = document.getElementById("activeChatInfoModal")
    if (!activeChatInfoModal) {
      activeChatInfoModal = document.createElement("div")
      activeChatInfoModal.classList.add("chat-modal", "active-chat-info-modal")
      activeChatInfoModal.setAttribute("id", "activeChatInfoModal")

      let activeChatInfoModalHtml = `<div class="chat-modal-content chat-modal-content--chat-info">
     <div class="chat-info-header">
      <div class="chat-info-header__title">
          Chat Info
      </div>
      <div class="chat-info-header__btn-container">
         

          <div class="chat-info-header__btn chat-info-header__btn--close-modal" id="closeActiveChatInfoModal">
              ${svg_closeBtn}
          </div>
      </div>
       </div>
      <div class="chat-info-pic chat-info-pic--hide-img">
      <img src=""
          alt="chat image">
          ${svg_chatPic}

        </div>
      <div class="chat-info-name">

         </div>
         <div class="chat-info-desc">

     </div>
        <div class="chat-info-btn-container">
      ${
        activeChatData.isGroupChat
          ? groupChatInfoBtnContainerHtml
          : userChatInfoBtnContainerHtml
      }
     </div>
  


         <div class="chat-info-member-container">
         
         </div>

        </div>`

      activeChatInfoModal.insertAdjacentHTML(
        "beforeend",
        activeChatInfoModalHtml
      )
      activeChatSection.insertAdjacentElement("beforeend", activeChatInfoModal)

      updateActiveChatInfoModal()
      initialiseEventForActiveChatInfo(activeChatInfoModal)
    } else {
      activeChatInfoModal.classList.remove("chat-modal--hide")
    }

    ///update activeChatInfoModal
    if (activeChatInfoModal.dataset.chatId !== chatId) {
      updateActiveChatInfoModal()
    }
  }
}
// initialise Event to Active Chat Info Modal
function initialiseEventForActiveChatInfo(activeChatInfoModal) {
  document
    .getElementById("chatInfoEditGroupChatBtn")
    .addEventListener("click", async () => {
      let { createGroupChatFormModal, updateGroupChatFormModalData } =
        await import("./createGroupChatFormModal.dev")

      createGroupChatFormModal("edit")
      updateGroupChatFormModalData(activeChatData)
    })
  document
    .getElementById("chatInfoClearChatHistoryBtn")
    .addEventListener("click", async () => {
      let { createClearChatHistoryModal } = await import(
        "./clearChatHistoryModal.dev"
      )
      createClearChatHistoryModal(activeChatData)
    })

  document
    .getElementById("chatInfoDeleteChatBtn")
    .addEventListener("click", async () => {
      let { createDeleteChatModal } = await import("./deleteChatModal.dev")
      createDeleteChatModal(activeChatData)
    })
  /////////////////////////
  document
    .getElementById("closeActiveChatInfoModal")
    .addEventListener("click", () => {
      activeChatInfoModal.classList.add("chat-modal--hide")
    })
  window.addEventListener("click", event => {
    if (event.target === activeChatInfoModal) {
      activeChatInfoModal.classList.add("chat-modal--hide")
    }
  })
}

export async function updateActiveChatInfoModal() {
  let activeChatInfoModal = document.getElementById("activeChatInfoModal")
  if (activeChatInfoModal) {
    activeChatInfoModal.dataset.chatId = activeChatData._id

    let activeChatInfoMemberContainer =
      activeChatInfoModal.getElementsByClassName(
        "chat-info-member-container"
      )[0]

    activeChatInfoMemberContainer.innerHTML = `<div class="chat-info-member-container__title">
      <span class="chat-info-member-container__count"></span>Members
      </div>
      
      <div class="chat-info-member-modal chat-info-member-modal--hide">
      <div class="chat-info-member-modal-content"> 
      
      <div class="chat-info-member-modal-content__option ">
      ${svg_viewUserProfile} View Profile
      </div>
      <div class="chat-info-member-modal-content__option chat-info-member-modal-content__option--admin-modal">
      ${svg_promoteUserInGroup} Promote To Admin
      </div>
      <div class="chat-info-member-modal-content__option chat-info-member-modal-content__option--admin-modal">
      ${svg_removeUserFromGroup} Remove From Group
      </div>
      <div class="chat-info-member-modal-content__option chat-info-member-modal-content__option--cancel-option" id="chatMemberModalCloseBtn">
      Cancel
      </div>
      
      </div>
      </div>
      `

    document
      .getElementById("chatMemberModalCloseBtn")
      .addEventListener("click", () => {
        activeChatInfoMemberContainer
          .getElementsByClassName("chat-info-member-modal")[0]
          .classList.add("chat-info-member-modal--hide")
      })

    window.addEventListener("click", e => {
      let chatInfoMemberModal =
        activeChatInfoMemberContainer.getElementsByClassName(
          "chat-info-member-modal"
        )[0]
      if (e.target == chatInfoMemberModal) {
        chatInfoMemberModal.classList.add("chat-info-member-modal--hide")
      }
    })
    /////////////
    // update active chat info member container count
    activeChatInfoMemberContainer.getElementsByClassName(
      "chat-info-member-container__count"
    )[0].textContent = activeChatData.currentChatMembers.length

    activeChatData.currentChatMembers.forEach(user => {
      let chatInfoMember = document.createElement("div")
      chatInfoMember.classList.add("chat-info-member")
      chatInfoMember.dataset.userId = user._id
      chatInfoMember.dataset.username = user.username
      chatInfoMember.insertAdjacentHTML(
        "beforeend",
        `<div class="chat-info-member__pic">
          <img src="${user.profile}" alt="">
       </div>
     <div class="chat-info-member__column chat-info-member__column--data">

     <div class="chat-info-member__row">
    <div class="chat-info-member__fullname"></div>
    <div class="chat-info-member__admin-tag">(admin)</div>
    </div>
    <div class="chat-info-member__username"></div>
    </div>
    <div class="chat-info-member__btn">
${svg_infoBlankBtn}
</div>`
      )
      activeChatInfoMemberContainer.insertAdjacentElement(
        "beforeend",
        chatInfoMember
      )

      chatInfoMember.getElementsByClassName(
        "chat-info-member__fullname"
      )[0].textContent = user.firstName + " " + user.lastName

      chatInfoMember.getElementsByClassName(
        "chat-info-member__username"
      )[0].textContent = "@" + user.username

      ///////////
      // attach event to active Chat Info modal members
      chatInfoMember
        .getElementsByClassName("chat-info-member__pic")[0]
        .addEventListener("click", () => {
          let username = chatInfoMember.dataset.username
          location.replace(`/user/${username}`)
        })
      chatInfoMember
        .getElementsByClassName("chat-info-member__fullname")[0]
        .addEventListener("click", () => {
          let username = chatInfoMember.dataset.username
          location.replace(`/user/${username}`)
        })
      chatInfoMember
        .getElementsByClassName("chat-info-member__btn")[0]
        .addEventListener("click", () => {
          activeChatInfoMemberContainer
            .getElementsByClassName("chat-info-member-modal")[0]
            .classList.remove("chat-info-member-modal--hide")
        })
    })

    // update chat admin members tag
    if (activeChatData.isGroupChat) {
      let checkLoginUserIsAdmin = false
      activeChatData.groupChatAdmin.forEach(userId => {
        activeChatInfoMemberContainer
          .querySelector(`.chat-info-member[data-user-id = "${userId}"]`)
          .classList.add("chat-info-member--admin")
        if (userId.toString() === loginUser._id.toString()) {
          checkLoginUserIsAdmin = true
        }
      })
      if (checkLoginUserIsAdmin === true) {
        activeChatInfoModal.classList.add("active-chat-info-modal--admin")
      } else {
        activeChatInfoModal.classList.add("active-chat-info-modal--member")
      }
    }

    ///update active chat info modal
    let chatInfoPic =
      activeChatInfoModal.getElementsByClassName("chat-info-pic")[0]

    if (
      activeChatData.hasOwnProperty("chatPic") &&
      activeChatData.chatPic !== ""
    ) {
      activeChatInfoModal.querySelector(".chat-info-pic img").src =
        activeChatData.chatPic

      chatInfoPic.classList.add("chat-info-pic--hide-svg")
      chatInfoPic.classList.remove("chat-info-pic--hide-img")
    } else {
      chatInfoPic.classList.add("chat-info-pic--hide-img")
      chatInfoPic.classList.remove("chat-info-pic--hide-svg")
    }

    activeChatInfoModal.getElementsByClassName(
      "chat-info-name"
    )[0].textContent = activeChatData.chatName

    activeChatInfoModal.getElementsByClassName(
      "chat-info-desc"
    )[0].textContent = activeChatData.chatDescription
  }
}

export async function createDeleteChatModal() {
  let activeChatInfoModal = document.getElementById("activeChatInfoModal")
  if (activeChatInfoModal) {
    let deleteChatModal = document.createElement("div")
    deleteChatModal.classList.add(
      "inner-modal",
      "inner-modal--hide",
      "inner-modal--delete-chat"
    )
    deleteChatModal.innerHTML = `
<div class="inner-modal-content">

<div class="inner-modal-header">
<div class="inner-modal-header__title">
Do you want to delete chat ?
</div>
</div>
<div class="inner-modal-main">
<div class="delete-chat-info">
After deleting chat all messages will be deleted and this chat is also deleted.

</div>

</div>
<div class="inner-modal-btns-container">
<div class="inner-modal-btn ">Cancel</div>
<div class="inner-modal-btn inner-modal-btn--action">Delete</div>
</div>
`

    activeChatInfoModal.insertAdjacentElement("afterbegin", deleteChatModal)
  }
}
